#########################################################################################################################
# WORKAROUND TO FIX "Invoke-RestMethod : The underlying connection was closed: An unexpected error occurred on a send." #
# C# class to create callback                                                                                           #
#########################################################################################################################

$code = @"
public class SSLHandler
{
    public static System.Net.Security.RemoteCertificateValidationCallback GetSSLHandler()
    {

        return new System.Net.Security.RemoteCertificateValidationCallback((sender, certificate, chain, policyErrors) => { return true; });
    }

}
"@

# Compile the class
Add-Type -TypeDefinition $code

# Disable checks using new class
[System.Net.ServicePointManager]::ServerCertificateValidationCallback = [SSLHandler]::GetSSLHandler()

##################
# WORKAROUND END #
##################


$appliance = Read-Host "OneView Address"
$userName = Read-Host "Username"
$password = Read-Host "Password"

$url = "https://$appliance"

$user = @{
    userName= "$($userName)"
    password= "$($password)"
    authnHost= "$appliance"
    authLoginDomain= "Local"
}

$json = $user | ConvertTo-Json 

$headers = New-Object "System.Collections.Generic.Dictionary[[String],[String]]"
$headers.Add("Content-type", 'application/json')
$headers.Add("Accept", 'application/json')
$uri = $url + '/rest/login-sessions'
$response = Invoke-RestMethod -Uri $uri -Method POST -Headers $headers -Body $json -ContentType 'application/json' 

$auth = New-Object "System.Collections.Generic.Dictionary[[String],[String]]"
$auth.Add("Auth", $response.sessionID)
$auth.Add("X-Api-Version", '2800')


$HPE_Logo = 'data:image/png;base64,'

