$NetIfObj | Add-Member -Type NoteProperty -Name app2Ipv4Addr -Value $ApplianceNetIf[0].applianceNetworks.app2Ipv4Addr
$NetIfObj | Add-Member -Type NoteProperty -Name ipv4Subnet -Value $ApplianceNetIf[0].applianceNetworks.ipv4Subnet
$NetIfObj | Add-Member -Type NoteProperty -Name ipv4Gateway -Value $ApplianceNetIf[0].applianceNetworks.ipv4Gateway
$NetIfObj | Add-Member -Type NoteProperty -Name ipv4NameServers -Value ($ApplianceNetIf[0].applianceNetworks.ipv4NameServers -join ', ')
