$ActiveAlerts_uri  = $url + "/rest/alerts?start=0&count=-1&filter=alertState EQ 'Active'"
$LockedAlerts_uri = $url + "/rest/alerts?start=0&count=-1&filter=alertState EQ 'Locked'"

$ActiveAlerts = Invoke-RestMethod -Method GET -Headers $auth -Uri $ActiveAlerts_uri
$LockedAlerts = Invoke-RestMethod -Method GET -Headers $auth -Uri $LockedAlerts_uri

foreach ($ActiveAlert in $ActiveAlerts.members) {
    $ActiveAlertResourceDesc = ""
    $ActiveAlertResource_uri = $url +$ActiveAlert.resourceUri
    $ActiveAlertResource = Invoke-RestMethod -Method GET -Headers $auth -Uri $ActiveAlertResource_uri

    if ($ActiveAlertResource.name) {
        $ActiveAlertResourceDesc = $ActiveAlertResource.name
    }
    else {
        $ActiveAlertResourceDesc = $ActiveAlert.physicalResourceType
    }

    $ActiveAlert | Add-Member NoteProperty resource($ActiveAlertResourceDesc)

}

foreach ($LockedAlert in $LockedAlerts.members) {
    $LockedAlertResourceDesc = ""
    $LockedAlertResource_uri = $url + $LockedAlert.resourceUri
    $LockedAlertResource = Invoke-RestMethod -Method GET -Headers $auth -Uri $LockedAlertResource_uri

    if ($LockedAlertResource.name) {
        $LockedAlertResourceDesc = $LockedAlertResource.name
    }
    else {
        $LockedAlertResourceDesc = $LockedAlert.physicalResourceType
    }

    $LockedAlert | Add-Member NoteProperty resource($LockedAlertResourceDesc)


}

$ActiveLockedAlerts = $ActiveAlerts.members + $LockedAlerts.members

$ActiveLockedAlertsHTML = $ActiveLockedAlerts | ConvertTo-Html -Fragment -Property description,resource,correctiveAction,severity,alertState,created -PreContent '<a id=Alerts><h2>2 - Active and Locked alerts</h2></a>'



