####################
# ENCLOSURE GROUPS #
####################

$EnclosureGroup_uri = $url + '/rest/enclosure-groups'
$EnclosureGroup = Invoke-RestMethod -Method GET -Headers $auth -Uri $EnclosureGroup_uri

$EnclosureGroupArray = @(
    for ($i=0; $i -lt $EnclosureGroup.members.Count; $i++) {
        $Interconnect_list = ""
        $IPv4Range_list = ""
       
        # Get interconnects that belongs to this EG
        foreach ($Interconnect in $($EnclosureGroup.members[$i].interconnectBayMappings)) {
            $Interconnect_LIG_uri = $url + $Interconnect.logicalInterconnectGroupUri
            $Interconnect_LIG_details = Invoke-RestMethod -Method GET -Headers $auth -Uri $Interconnect_LIG_uri
            $Interconnect_list = $Interconnect_list + ", Interconnect " + $Interconnect.interconnectBay + " ($($Interconnect_LIG_details.name))"
        }

        # Get IPv4 ranges that belongs to this EG
        foreach ($IPv4Range in $($EnclosureGroup.members[$i].ipRangeUris)) {
            $IPv4Range_uri = $url + $IPv4Range
            $IPv4Range_details = Invoke-RestMethod -Method GET -Headers $auth -Uri $IPv4Range_uri
            $IPv4Range_list = $IPv4Range_list + ", " + $IPv4Range_details.name
        }

    $Interconnect_list = $Interconnect_list -replace "^\, ", ""
    $IPv4Range_list = $IPv4Range_list -replace "^\, ", ""
    [pscustomobject]@{name=$($EnclosureGroup.members[$i].name);enclosureCount=$($EnclosureGroup.members[$i].enclosureCount);interconnects=$Interconnect_list;IPv4Ranges=$IPv4Range_list}
    }
)
$EnclosureGroupHTML = $EnclosureGroupArray | Sort-Object -Property name | ConvertTo-Html -Fragment -PreContent '<br><a id=EnclosureGroups><h2>8 - Enclosure Groups</h2></a>'



###############################
# LOGICAL INTERCONNECT GROUPS #
###############################

$LogicalInterconnectGroup_custom_auth = $auth
$LogicalInterconnectGroup_custom_auth."X-Api-Version"="2800"

# ---------------
# LIG ETHERNET/FC
# ---------------

$LogicalInterconnectGroup_uri = $url + '/rest/logical-interconnect-groups'
$LogicalInterconnectGroup = Invoke-RestMethod -Method GET -Headers $LogicalInterconnectGroup_custom_auth -Uri $LogicalInterconnectGroup_uri
$LogicalInterconnectGroupArray = @(
    
    # ------------
    # ETHERNET LIG
    # ------------

    $UplinkSetHTMLArray = @()

    foreach ($LIG in $($LogicalInterconnectGroup.members)) {
        
        $UplinkSet_ReplaceMe = ""
        $UplinkSet_TempName = ""
        $LACP_TIMER = ""

        $Interconnect_type_uri = $url + $LIG.interconnectMapTemplate.interconnectMapEntryTemplates.permittedInterconnectTypeUri
        $Interconnect_type = Invoke-RestMethod -Method GET -Headers $auth $Interconnect_type_uri

        foreach ($UplinkSet in $($LIG.uplinkSets)) {

            # --------------------------------------------
            # Get Network names for each UplinkSet - begin
            # --------------------------------------------

            $UplinkSet_Networks = ""

            foreach ($NetworkNet_uri in $UplinkSet.networkUris) {
                $NetworkNet_uri = $url + $NetworkNet_uri
                $NetworkNet_details = Invoke-RestMethod -Method GET -Headers $auth $NetworkNet_uri
                $UplinkSet_Networks = $UplinkSet_Networks + ", " + $NetworkNet_details.name
            }

            $UplinkSet_Networks = $UplinkSet_Networks -replace "^, ", ""
            
            # --------------------------------------------
            # Get Network names for each UplinkSet - end
            # --------------------------------------------


            # -----------------------------------------------
            # Get NetworkSet names for each UplinkSet - begin
            # -----------------------------------------------

            $UplinkSet_NetworkSets = ""

            foreach ($NetworkSetNet_uri in $UplinkSet.networkSetUris) {
                $NetworkSetNet_uri = $url + $NetworkSetNet_uri
                $NetworkSetNet_details = Invoke-RestMethod -Method GET -Headers $auth $NetworkSetNet_uri
                $UplinkSet_NetworkSets = $UplinkSet_NetworkSets + ", " + $NetworkSetNet_details.name
            }

            $UplinkSet_NetworkSets = $UplinkSet_NetworkSets -replace "^, ", ""
            
            # ---------------------------------------------
            # Get NetworkSet names for each UplinkSet - end
            # ---------------------------------------------

            $Port_List = ""

            $Network_type = $UplinkSet.networkType

            foreach ($Port in $($UplinkSet.logicalPortConfigInfos)) {

                $ENCLOSURE     = $Port.logicalLocation.locationEntries | Where-Object {$_.type -eq 'Enclosure'}
                $BAY           = $Port.logicalLocation.locationEntries | Where-Object {$_.type -eq 'Bay'}
                $PORT_RELATIVE = $Port.logicalLocation.locationEntries | Where-Object {$_.type -eq 'Port'}
                $PORT_Q        = $Interconnect_type.portInfos | Where-Object {$_.portNumber -eq $PORT_RELATIVE.relativeValue}
                $LACP_TIMER    = $UplinkSet.lacpTimer
                
                $Port_List = $Port_List + ", [Enclosure " + $ENCLOSURE.relativeValue + "/Bay " + $BAY.relativeValue + "/Port " + $PORT_Q.portName + "]"
                                
            }

            if ($Network_type -ne 'Ethernet') {
                $LACP_TIMER = "N/A"
            }

            $Port_List = $Port_List -replace "^, ", ""
           
            $UplinkSet_DetailsArray = @(
                [pscustomobject]@{name=$($UplinkSet.name);type=$Network_type;ports=$Port_List;lacpTimer=$LACP_TIMER;networks=$UplinkSet_Networks;networkSets=$UplinkSet_NetworkSets}
            )
            $UplinkSet_DetailsArrayHTML = $UplinkSet_DetailsArray | ConvertTo-Html -Fragment

            $UplinkSet_TempName = $UplinkSet.name + $LIG.name
            
            # Adding '@@@' to replace string
            $UplinkSet_ReplaceMe = $UplinkSet_ReplaceMe + "@@@" + $UplinkSet.name + $LIG.name

            $UplinkSetHTMLArray += @(
                [pscustomobject]@{name=$($UplinkSet_TempName);htmlCode=$($UplinkSet_DetailsArrayHTML)}
            )

        }

        $UplinkSet_ReplaceMe = $UplinkSet_ReplaceMe -replace "^@@@", ""
        [pscustomobject]@{name=$($LIG.name);uplinkSet=$UplinkSet_ReplaceMe}

    }

)

$LogicalInterconnectGroupHTML = $LogicalInterconnectGroupArray | Sort-Object -Property name | ConvertTo-Html -Fragment -PreContent '<a id=LogicalInterconnectGroupEthFC><h2>9.1 - Logical Interconnect Groups (ETH/FC)</h2></a>'
$LogicalInterconnectGroupHTML = $LogicalInterconnectGroupHTML -replace "@@@", "<br>"

# Replacing LIG information to HTML (table inside table)
foreach ($LIGtoReplace in $UplinkSetHTMLArray) {
    $LogicalInterconnectGroupHTML = $LogicalInterconnectGroupHTML -replace $LIGtoReplace.name, $LIGtoReplace.htmlCode
}

# -------
# LIG SAS
# -------

$LogicalInterconnectGroupSAS_uri = $url + '/rest/sas-logical-interconnect-groups'
$LogicalInterconnectGroupSAS = Invoke-RestMethod -Method GET -Headers $LogicalInterconnectGroup_custom_auth -Uri $LogicalInterconnectGroupSAS_uri
$LogicalInterconnectGroupSASArray = @(

    foreach ($LIGSAS in $($LogicalInterconnectGroupSAS.members)) {

        $SASInterconnectList = ""

        foreach ($SASInterconnect in $($LIGSAS.interconnectMapTemplate.interconnectMapEntryTemplates)) {
            $SASInterconnectBay = $SASInterconnect.logicalLocation.locationEntries | Where-Object {$_.type -eq 'Bay'}
            $SASInterconnectEnc = $SASInterconnect.logicalLocation.locationEntries | Where-Object {$_.type -eq 'Enclosure'}
            $SASInterconnectList = $SASInterconnectList + ", " + "[Enclosure " + $SASInterconnectEnc.relativeValue + "/ Interconnect " + $SASInterconnectBay.relativeValue + "]"
        }

        $SASInterconnectList = $SASInterconnectList -replace "^, ", ""

        [pscustomobject]@{name=$($LIGSAS.name);interconnectBaySet=$($LIGSAS.interconnectBaySet);interconnects=$SASInterconnectList}

    }

)

$LogicalInterconnectGroupSASHTML = $LogicalInterconnectGroupSASArray | ConvertTo-Html -Fragment -PreContent '<a id=LogicalInterconnectGroupSAS><h2>9.2 - Logical Interconnect Groups (SAS)</h2></a>'



#########################
# LOGICAL INTERCONNECTS #
#########################

# ----------------------------
# LOGICAL INTERCONNECTS ETH/FC
# ----------------------------

$LogicalInterconnect_uri = $url + '/rest/logical-interconnects'
$LogicalInterconnect = Invoke-RestMethod -Method GET -Headers $auth -Uri $LogicalInterconnect_uri
$LIPortInfo = @()

foreach ($LI in $($LogicalInterconnect.members)) {

    $LIInterconnect_uri = $url + $LI.interconnects
    $LIInterconnect_details = Invoke-RestMethod -Method GET -Headers $auth -Uri $LIInterconnect_uri
    $LIconsistencyStatus = $LI.consistencyStatus
        
    foreach ($LIPort in $($LIInterconnect_details.ports)) {
                
        if ($($LIPort.associatedUplinkSetUri)) {
            $UplinkSet_uri = $url + $LIPort.associatedUplinkSetUri
            $UplinkSet_details = Invoke-RestMethod -Method GET -Headers $auth -Uri $UplinkSet_uri
                    
            if ($($UplinkSet_Details.logicalInterconnectUri -eq $LI.uri)) {

                $PortFullDescription = "[" + $LIPort.interconnectName + ", Port " + $LIPort.portName + "]"
                $PortFullDescription = $PortFullDescription -replace ", ", "/"
                $portNeighbor = $LIPort.remoteSystemName + " " + $LIPort.remotePortId

                $LIPortInfo += @(
                    [pscustomobject]@{LI_Name=$($LI.name);LIstatus=$($LI.status);LIconsistencyStatus=$($LIconsistencyStatus);UplinkSetName=$($UplinkSet_details.name);type=$($UplinkSet_details.networkType);portName=$($PortFullDescription);portState=$($LIPort.portStatus) + "/" +  $($LIPort.portStatusReason);neighbor=$portNeighbor;lagId=$($LIPort.lagId);operationalSpeed=$($LIPort.operationalSpeed)}
                )
            }
        }
    }
}

$uniqueLINames = $LIPortInfo.LI_Name | Select-Object -Unique | Sort-Object

$LIMasterArray = @()
$LI_UPS_Array = @()
foreach ($LIUniqueName in $($uniqueLINames)) {


    $uniqueLIObjs = $LIPortInfo | Where-Object {$_.LI_Name -eq $($LIUniqueName)}
    $uniqueUplinkSetNames = $uniqueLIObjs.UplinkSetName | Select-Object -Unique | Sort-Object
    $LIconsistencyObj = $uniqueLIObjs.LIconsistencyStatus | Select-Object -Unique
    
    foreach ($uniqueUPS in $uniqueUplinkSetNames) {

        $LIPortDetails = ""
        $LIPortLagID = ""
        $LIObjsPorts = $LIPortInfo | Where-Object {$_.LI_Name -eq $($LIUniqueName) -and $_.UplinkSetName -eq $uniqueUPS} 
        $LIUPSType = $LIObjsPorts.type | Select-Object -Unique

        foreach ($LIPortName in $($LIObjsPorts.portName)) {

            $LIPortsUPS = $LIPortInfo | Where-Object {$_.LI_Name -eq $($LIUniqueName) -and $_.UplinkSetName -eq $uniqueUPS -and $_.portName -eq $LIPortName}
            $LIPortSpeed = $LIPortsUPS.operationalSpeed -replace "Speed", ""

            if ($LIUPSType -eq 'Ethernet') {
                $LIPortLagID = $LIPortsUPS.lagId
            }
            else{
                $LIPortLagID = "None"
            }

            $LIPortDetails = $LIPortDetails + $LIPortsUPS.portName + " (" + $LIPortsUPS.portState + ")" + " speed=" + $LIPortSpeed + " lagId=" + $LIPortLagID + " neighbor=" + $LIPortsUPS.neighbor + "@@@br"
        }

        $LI_UPS_Array += @(
            [pscustomobject]@{LI_Name=$LIUniqueName;UplinkSet_Name=$uniqueUPS;UplinkSet_Type=$LIUPSType;PortDetails=$LIPortDetails}
        )

    }

    $LIMasterArray += @(
        $LIReplaceString = $LIUniqueName + "@@@"
        [pscustomobject]@{LI_Name=$LIUniqueName;consistencyStatus=$LIconsistencyObj;UplinkSet_Details=$LIReplaceString}
    )

}

$LIMasterArrayHTML = $LIMasterArray | ConvertTo-Html -Fragment

foreach ($LIUnqName in $($uniqueLINames)) {
    $LISelObj = $LI_UPS_Array | Where-Object {$_.LI_Name -eq $LIUnqName}
    $LISelObjHTML = $LISelObj | ConvertTo-Html -Property UplinkSet_Name,UplinkSet_Type,PortDetails -Fragment
    $LIMasterArrayHTML = $LIMasterArrayHTML -replace "$($LIUnqName)@@@", $LISelObjHTML
    $LIMasterArrayHTML = $LIMasterArrayHTML -replace "@@@br", "<br>"
}



# -----------------------
# SERVER PROFILE TEMPLATE
# -----------------------

$ServerProfileTemplate_uri = $url + '/rest/server-profile-templates'
$ServerProfileTemplate = Invoke-RestMethod -Method GET -Headers $auth -Uri $ServerProfileTemplate_uri
$SPTArray = @()

foreach ($SPT in $($ServerProfileTemplate.members)) {

    $SPTConnectionInfo = ""
    $SPTLocalStorageInfo = ""
    $SPTLocalDiskInfo = ""
    $SPTBiosParameterInfo = ""
    $ILOLocalAccountInfo = ""

    $SPTFirmwareBaseline = $SPT.firmware.firmwareBaselineUri
    $SPTFirmwareBaseline = $SPTFirmwareBaseline.split("/",4)[-1]
    $SPTFirmwareInfo = "firmwareInstallType=" + $SPT.firmware.firmwareInstallType + "@@@br" + " forceInstallFirmware=" + $SPT.firmware.forceInstallFirmware + "@@@br" + " fwBaseline=" + $SPTFirmwareBaseline


    # --------------------
    # Server hardware type
    # --------------------

    $SPTServerHardwareType_uri = $url + $SPT.serverHardwareTypeUri
    $SPTServerHardwareType = Invoke-RestMethod -Method GET -Headers $auth -Uri $SPTServerHardwareType_uri


    # ----------
    # Connection
    # ----------
    foreach ($SPTConnection in $($SPT.connectionSettings.connections)) {

        $SPTConnectionNetInfo_uri = $url + $SPTConnection.networkUri
        $SPTConnectionNetInfo = Invoke-RestMethod -Method GET -Headers $auth -Uri $SPTConnectionNetInfo_uri
        
        $SPTConnectionInfo += "id=" + $SPTConnection.id + "@@@br" + " type=" + $SPTConnection.functionType + "@@@br" + " name=" + $SPTConnection.name + "@@@br" + " port=" + $SPTConnection.portId + "@@@br" + " net=" + $SPTConnectionNetInfo.name + "@@@br" + " vlan= " + $SPTConnectionNetInfo.vlanId + "@@@br" + " requestedMbps= " + $SPTConnection.requestedMbps + "@@@br@@@br"
    }

    # -------------
    # Local storage 
    # -------------
    foreach ($SPDiskController in $($SPT.localStorage.controllers)) {

        foreach ($SPTLocalDisk in $($SPDiskController.logicalDrives)) {
            $SPTLocalDiskInfo += "logicalDriveName=" + $SPTLocalDisk.name + "@@@br" + " logicalDriveRaid=" + $SPTLocalDisk.raidLevel + "@@@br" + " bootable=" + $SPTLocalDisk.bootable + "@@@br" + " numPhysDrvs=" + $SPTLocalDisk.numPhysicalDrives + "@@@br" + " driveTechnology=" + $SPTLocalDisk.driveTechnology
        }

        $SPTLocalStorageInfo += "controllerSlot=" + $SPDiskController.deviceSlot + "@@@br" + " controllerMode=" + $SPDiskController.mode + "@@@br" +  " initializeController= " + $SPDiskController.initialize + "@@@br" + $SPTLocalDiskInfo + "@@@br@@@br"

    }

    # -------------
    # Bios settings
    # -------------

    $SPTBiosParameterInfo = ""

    if ($SPT.bios.manageBios -eq $false) {
        $SPTBiosParameterInfo = "Not managed"
    }
    else {
        foreach ($SPTBiosParameter in $($SPT.bios.overriddenSettings)) {
            $SPTBiosParameterInfo += $SPTBiosParameter.id + "=" + $SPTBiosParameter.value + "@@@br"
        }
    }

    # ------------
    # ILO settings
    # ------------
    # Currently only checking:
    #     - if local accounts where created
    #     - if Administrator account is managed
    
    if ($SPT.managementProcessor.manageMp -eq $false) {
        $ILOLocalAccountInfo = "Not managed"
    }
    else {

        foreach ($SPTILOSetting in $($SPT.managementProcessor.mpSettings)) {

            foreach ($ILOSetingArg in $($SPTILOSetting.args)) {

                # -------------------------------------
                # If local account created, get details
                # -------------------------------------
                if ($($SPTILOSetting.settingType) -eq 'LocalAccounts') {

                    foreach ($ILOArg in $($ILOSetingArg.localAccounts)) {
                        $ILOLocalAccountInfo += "userName=" + $ILOArg.userName + "@@@br" +  " displayName=" + $ILOArg.displayName + "@@@br" + " userConfigPriv=" + $ILOArg.userConfigPriv + "@@@br" + " remoteConsolePriv=" + $ILOArg.remoteConsolePriv + "@@@br" + " virtualMediaPriv=" + $ILOArg.virtualMediaPriv + "@@@br" + " virtualPowerAndResetPriv=" + $ILOArg.virtualPowerAndResetPriv + "@@@br" + " ILOConfigPriv=" + $ILOArg.iLOConfigPriv + "@@@br@@@br"
                    }

                }

                # -----------------------------------
                # If Administrator account is managed
                # -----------------------------------
                if ($SPTILOSetting.settingType -eq 'AdministratorAccount') {
                    $ILOLocalAccountInfo += "adminAccountManaged=True @@@brdeleteAdministratorAccount=" + $ILOSetingArg.deleteAdministratorAccount + "@@@br@@@br"
                }

            }

        }
    }

    $SPTArray += @(
        [pscustomobject]@{name=$($SPT.name);serverHardwareType=$SPTServerHardwareType.name;firmwareInfo=$SPTFirmwareInfo;connectionInfo=$SPTConnectionInfo;localStorage=$SPTLocalStorageInfo;biosOverriddenSettings=$SPTBiosParameterInfo;iloConfig=$ILOLocalAccountInfo}
    )
}

$SPTArrayHTML = $SPTArray | ConvertTo-Html -Fragment -PreContent '<br><a id=ServerProfileTemplates><h2>11 - Server profile templates</h2></a>'
$SPTArrayHTML = $SPTArrayHTML -replace "@@@br", "<br>"



# ---------------
# Server profiles
# ---------------


$ServerProfiles_uri = $url + '/rest/server-profiles'
$ServerProfiles = Invoke-RestMethod -Method GET -Headers $auth -Uri $ServerProfiles_uri

$SPArray = @()

foreach ($SP in $ServerProfiles.members) {

    $ServerProfileHardware_uri = $url + $SP.serverHardwareUri
    $ServerProfileHardware = Invoke-RestMethod -Method GET -Headers $auth -Uri $ServerProfileHardware_uri

    $SPTemplate_uri = $url + $SP.serverProfileTemplateUri
    $SPTemplate = Invoke-RestMethod -Method GET -Headers $auth -Uri $SPTemplate_uri

    if ($SP.templateCompliance -eq 'Compliant') {
        $SPTemplateConsistency = "Consistent with template"
    }
    else {
        $SPTemplateConsistency = "Inconsistent with template"
    }

    $SPArray += @(
        [pscustomobject]@{name=$($SP.name);serverHardware=$ServerProfileHardware.name;serverProfileTemplate=$SPTemplate.name;templateConsistency=$SPTemplateConsistency;firmwareConsistency=$SP.firmware.consistencyState;power=$ServerProfileHardware.powerState}
    )

}

$SPArrayHTML = $SPArray | ConvertTo-Html -Fragment  -PreContent '<br><a id=ServerProfiles><h2>12 - Server profiles</h2></a>'

